import React, { useState, useEffect } from "react";
import { Redirect, Link } from "react-router-dom";
import "./profile.css";
import Axios from "axios";
function Profile() {
  const [name, setName] = useState("Loading...");
  const [email, setEmail] = useState("Loading...");
  const [address, setAddress] = useState("Loading...");
  const [id, setId] = useState("Loading...");
  useEffect(() => {
    if (localStorage.getItem("user-auth-token") != null) {
      const token = localStorage.getItem("user-auth-token");
      let config = {
        headers: {
          "user-auth-token": token,
        },
      };
      Axios.get("http://localhost:4000/userdetails", config)
        .then((response) => {
          console.log(response);
          console.log(response.data.msg);
          setName(response.data.msg.name);
          setEmail(response.data.msg.email);
          setAddress(response.data.msg.address);
          setId(response.data.msg._id);

          //   setSuccess(true);
        })
        .catch((error) => {
          console.log(error);
          console.log(error.response.data.msg);
          //   setStatus(error.response.data.msg);

          window.scrollTo(0, 0);
        });
    }
  }, []);

  return (
    <div className="container emp-profile">
      {localStorage.getItem("user-auth-token") ? null : (
        <Redirect to="/login" />
      )}
      <div className="row">
        <div className="col-md-4">
          <div className="profile-img">
            <img
              src="data:image/png;base64,"
              alt=""
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="profile-head">
            <h5>{name}</h5>
          </div>
        </div>
        <div className="col-md-2">
          <Link to="/editprofile">
            <button
              type="submit"
              className="profile-edit-btn"
              name="btnAddMore"
              defaultValue="Edit Profile"
            >
              Edit
            </button>
          </Link>{" "}
        </div>
      </div>
      <div className="row">
        <div className="col-md-4"></div>
        <div className="col-md-8">
          <div className="tab-content profile-tab" id="myTabContent">
            <div
              className="tab-pane fade show active"
              id="home"
              role="tabpanel"
              aria-labelledby="home-tab"
            >
              <div className="row">
                <div className="col-md-6">
                  <label>User Id</label>
                </div>
                <div className="col-md-6">
                  <p>{id}</p>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <label>Name</label>
                </div>
                <div className="col-md-6">
                  <p>{name}</p>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <label>Email</label>
                </div>
                <div className="col-md-6">
                  <p>{email}</p>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <label>Address</label>
                </div>
                <div className="col-md-6">
                  <p>{address}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
