import "../profile.css";
import React, { useState, useEffect } from "react";
import { Redirect, Link } from "react-router-dom";
import Axios from "axios";
function EditSellerProfile() {
  const [name, setName] = useState("Loading...");
  const [email, setEmail] = useState("Loading...");
  const [address, setAddress] = useState("Loading...");
  const [area, setArea] = useState("Loading...");
  const [cruisine, setCruisine] = useState("Loading...");
  const [minimumOrder, setMinimumOrder] = useState("Loading...");
  const [tel, setTel] = useState("Loading...");
  const [id, setId] = useState("Loading...");
  const [restaurantName, setRestaurantName] = useState("Loading...");
  const [success, setSuccess] = useState(false);

  function update() {
    if (localStorage.getItem("seller-auth-token") != null) {
      const token = localStorage.getItem("seller-auth-token");
      let config = {
        headers: {
          "user-auth-token": token,
        },
      };
      let formData = new FormData();
      formData.append("ownerName", name);
      formData.append("address", address);
      formData.append("email", email);
      formData.append("area", area);
      formData.append("cruisine", cruisine);
      formData.append("minimumOrder", minimumOrder);
      formData.append("tel", tel);
      formData.append("restaurantName", restaurantName);
      Axios.post("http://localhost:4000/updatesellerdetails", formData, config)
        .then((response) => {
          setSuccess(true);
        })
        .catch((error) => {
          console.log(error);
          console.log(error.response.data.msg);
          //   setStatus(error.response.data.msg);

          window.scrollTo(0, 0);
        });
    }
  }
  useEffect(() => {
    if (localStorage.getItem("seller-auth-token") != null) {
      const token = localStorage.getItem("seller-auth-token");
      let config = {
        headers: {
          "seller-auth-token": token,
        },
      };
      Axios.get("http://localhost:4000/sellerdetails", config)
        .then((response) => {
          setName(response.data.msg.ownerName);
          setEmail(response.data.msg.email);
          setAddress(response.data.msg.address);
          setId(response.data.msg._id);

          setArea(response.data.msg.area);
          setCruisine(response.data.msg.cruisine);
          setMinimumOrder(response.data.msg.minimumOrder);
          setTel(response.data.msg.tel);
          setRestaurantName(response.data.msg.restaurantName);

          //   setSuccess(true);
        })
        .catch((error) => {
          console.log(error);
          console.log(error.response.data.msg);
          //   setStatus(error.response.data.msg);

          window.scrollTo(0, 0);
        });
    }
  }, []);

  return (
    <div className="container emp-profile">
      {localStorage.getItem("seller-auth-token") ? null : (
        <Redirect to="/sellerlogin" />
      )}
      {success ? <Redirect to="/sellerprofile" /> : null}

      <div className="row">
        <div className="col-md-4">
          <div className="profile-img">
            <img
              src="data:image/png;base64,"
              alt=""
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="profile-head">
            <h5>{name}</h5>
          </div>
        </div>
        <div className="col-md-2">
          <Link to="/sellerprofile">
            <button
              type="submit"
              className="profile-edit-btn"
              name="btnAddMore"
              defaultValue="Edit Profile"
              onClick={() => update()}
            >
              Update
            </button>
          </Link>{" "}
        </div>
      </div>
      <div className="row">
        <div className="col-md-4"></div>
        <div className="col-md-8">
          <div className="tab-content profile-tab" id="myTabContent">
            <div
              className="tab-pane fade show active"
              id="home"
              role="tabpanel"
              aria-labelledby="home-tab"
            >
              <div className="row">
                <div className="col-md-6">
                  <label>User Id</label>
                </div>
                <div className="col-md-6">
                  <p>{id}</p>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <label>Name</label>
                </div>
                <div className="col-md-6">
                  <input
                    value={name}
                    onChange={(e) => {
                      setName(e.target.value);
                    }}
                  />
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <label>Email</label>
                </div>
                <div className="col-md-6">
                  <input
                    value={email}
                    onChange={(e) => {
                      setEmail(e.target.value);
                    }}
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-md-6">
                  <label>Restaurant Name</label>
                </div>
                <div className="col-md-6">
                  <input
                    value={restaurantName}
                    onChange={(e) => {
                      setRestaurantName(e.target.value);
                    }}
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-md-6">
                  <label>Area</label>
                </div>
                <div className="col-md-6">
                  <input
                    value={area}
                    onChange={(e) => {
                      setArea(e.target.value);
                    }}
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-md-6">
                  <label>Minimum Order</label>
                </div>
                <div className="col-md-6">
                  <input
                    value={minimumOrder}
                    onChange={(e) => {
                      setMinimumOrder(e.target.value);
                    }}
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-md-6">
                  <label>Cruisine</label>
                </div>
                <div className="col-md-6">
                  <input
                    value={cruisine}
                    onChange={(e) => {
                      setCruisine(e.target.value);
                    }}
                  />
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <label>Address</label>
                </div>
                <div className="col-md-6">
                  <input
                    value={address}
                    onChange={(e) => {
                      setAddress(e.target.value);
                    }}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EditSellerProfile;
